import styled from "styled-components";

export const CardDetailContainer = styled.div`
    min-height: 100vh;
    width: 100vw;
    border: 1px solid green;
    display: flex;
    justify-content: center;
    align-items: center;
`

export const CardDetail = styled.div`
    min-height: 300px;
    min-width: 350px;
    background-color: white;
    border-radius: 3px;
`
