    return (
        <SignContainer>
            <h1>Let's sign in!</h1>

            <form onSubmit={handleSubmit(onSubmit)}>

            <input {...register("email", { required: true })} />
            {errors.email && <span>This field is required</span>}

            <input {...register("password", { required: true })} />
            {errors.password && <span>This field is required</span>}

            <input type="submit" />
            </form>

        </SignContainer>
    )
}
