import { SignContainer } from "../styles/Sign.style"
import { Link } from "react-router-dom"

export default function SignUp() {

    function signUp() {

        fetch("https://api.edu.etherial.dev/apijsv2/auth", {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: mail,
                password : mdp
            })
        }).then(function(response) {

            response.json().then(function(json) {

               console.log(json)

                if (json["status"] === 200) {

                    localStorage.setItem("token", json["data"]["token"])
                    localStorage.setItem("mail", mail)

                    header.style.display = "none"
                    formCo.style.display = "none"
                    ensemble.style.display = "block"
                    form.style.display = "block"
                    blocDeco.style.display = "flex"

                    Refresh()
                }

            })

        })

    }

    return (
        <SignContainer>
            <h1>Let's sign up !</h1>

                <form method="POST">
                    <input type="text" placeholder="Choose your title film..."/>
                    <input type="text" placeholder="Select a category like Drama, Action, Comedy..."/>
                    <input type="text" placeholder="Give your url image movie..."/>
                    <button>Submit</button>
                    <Link to="/sign-in">Already sign up ? Click here !</Link>
                </form>

        </SignContainer>
    )
}
